import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';
import Home from './pages/Home';
// import Register from './pages/Register.jsx';
import Authentication from './pages/Authentication';
import Login from './component/login/Login';
import Register from './component/register/Register';
import { useEffect, useState } from 'react';
import ChatPage from './pages/ChatPage';
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState({});

  // Function to set the login state and store user data
  const handleLogin = (data) => {
    setIsLoggedIn(true);
    setUserData(data);

    // Store user data in localStorage
    localStorage.setItem('userData', JSON.stringify(data));
  };

  // Function to set the logout state and clear user data from localStorage
  const handleLogout = () => {
    setIsLoggedIn(false);

    // Clear user data from localStorage
    localStorage.removeItem('userData');
  };

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setIsLoggedIn(true);
    }

    // Retrieve user data from localStorage, if available
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      setUserData(JSON.parse(storedUserData));
    }
  }, []);
  return (
    <Router>
      <Routes>
        <Route
          exact
          path="/"
          element={isLoggedIn ? <ChatPage userData = {userData}/> : <Home handleLogin={handleLogin} isLoggedIn={isLoggedIn} />}
        />
        <Route exact path="/login" element={<Login handleLogin={handleLogin} />} />
        <Route exact path="/register" element={<Register/>} />
      </Routes>
    </Router>
  );
}

export default App;
