import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';
import Home from './pages/Home';
// import Register from './pages/Register.jsx';
import Authentication from './pages/Authentication';
import Login from './component/login/Login';
import Register from './component/register/Register';
import { useEffect, useState } from 'react';
import ChatPage from './pages/ChatPage';
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Function to set the login state
  const handleLogin = ({data}) => {
    setIsLoggedIn(true);
  };

  // Function to set the logout state
  const handleLogout = () => {
    setIsLoggedIn(false);
  };


  useEffect(() => {
    const storedToken = localStorage.getItem('token'); // Replace 'token' with the key you used
    if (storedToken) {
      setIsLoggedIn(true);
    }
  }, []);
  return (
    <Router>
      <Routes>
        <Route
          exact
          path="/"
          element={isLoggedIn ? <ChatPage /> : <Home handleLogin={handleLogin} isLoggedIn={isLoggedIn} />}
        />
        <Route exact path="/login" element={<Login handleLogin={handleLogin} />} />
        <Route exact path="/register" element={<Register/>} />
      </Routes>
    </Router>
  );
}

export default App;
