import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';
import Home from './pages/Home';
// import Register from './pages/Register.jsx';
import Authentication from './pages/Authentication';
import Login from './component/login/Login';
import Register from './component/register/Register';
import { useEffect, useState } from 'react';
import ChatPage from './component/chatpage/ChatPage';
function App() {
  const storedUserData = JSON.parse(localStorage.getItem('userData'));
  // console.log("storeUserData",storedUserData)
  const [isLoggedIn, setIsLoggedIn] = useState(!!storedUserData);
  const [userData, setUserData] = useState(storedUserData || {});

  // Function to set the login state and store user data
  const handleLogin = (data) => {
    setIsLoggedIn(true);
    setUserData(data);

    // Store user data in localStorage
    localStorage.setItem('userData', JSON.stringify(data));
  };

  // Function to set the logout state and clear user data from localStorage
  const handleLogout = () => {
    setIsLoggedIn(false);

    // Clear user data from localStorage
    localStorage.removeItem('userData');
  };

  useEffect(() => {
    // const storedToken = localStorage.getItem('token');
    // if (storedToken) {
    //   setIsLoggedIn(true);
    // }

    // Retrieve user data from localStorage, if available
    console.log(isLoggedIn)
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      // console.log("hi",storedUserData)
      // console.log("hello",JSON.parse(storedUserData))
      setUserData(JSON.parse(storedUserData));
    }
  }, []);

  useEffect(() => {
    // Emit a "setup" event to the server to set up the socket connection
    socket.emit('setup', {iser:userData.other});

    // Listen for a "connected" event from the server
    socket.on('connected', () => {
      setSocketConnected(true);
      console.log('Socket is connected');
    });

    // Cleanup the socket connection when the component unmounts
    return () => {
      socket.disconnect();
    };
  }, []);
  return (
    <Router>
      <Routes>
        <Route
            exact
            path="/"
            element={<Authentication/>}
        />
        <Route
          exact
          path="/chat"
          element={isLoggedIn ? <ChatPage userData = {userData} handleLogout={handleLogout}/> : <Home handleLogin={handleLogin} isLoggedIn={isLoggedIn} />}
        />
        <Route exact path="/register" element={<Register/>} />
      </Routes>
    </Router>
  );
}

export default App;
