import React, { useState } from 'react';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import '../register/register.css'
import { Link } from 'react-router-dom';
const Login = ({change, setChange}) => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const [show, setShow] = useState(false)
  const showPassword = (e) => {
    setShow(!show)
  }
  const handleChange = (e) => {
    const { name, value} = e.target;
    // Check if the input is a file input and handle it separately

      setFormData({
        ...formData,
        [name]: value,
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here, e.g., send data to the server
    console.log(formData);
  };

  return (
    <div className="register">
      <div className="register-container">
        <div className='register-body'>
          <div className="profile-pic">
            <WhatsAppIcon className='icon'/>
          </div>
          <div className="form">
            <h3>Login Account</h3>
            <form onSubmit={handleSubmit}>
              <div className="input-feild">
                <label htmlFor="username">Name</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  placeholder="Username"
                  value={formData.username}
                  onChange={handleChange}
                />
              </div>

              <div className="input-feild">
                <label htmlFor="password">Password</label>
                <div className='passwordtype'>
                  <input
                    type={show ? "text" : "password"}
                    id="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                  />
                  <button onClick={showPassword}>
                    {formData.password === "" ? "Show" :show ? "Hide" : "Show"}
                  </button>
                </div>

              </div>

              <div className='button'>
                 <button button type="submit">Login</button>
              </div>

            </form>
            <div className='loginroute'>
              <p>Create an Account: </p>
              <Link onClick={()=> setChange(!change)}>
                Sign up
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
