import React, { useEffect, useState } from 'react'
import '../../pages/auth.css'
import './chatpage.css'
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import GroupsIcon from '@mui/icons-material/Groups';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';
import MyChat from '../mychat/MyChat';
const ChatPage = ({userData,handleLogout}) => {

  const [dropDown, setdropDown] = useState(false)
  const [search, setSearch] = useState(localStorage.getItem('search') || '');
  const [searchResult,setSearchResult] = useState([])
  const [error, setError] = useState(null); 
  const [loding, setLoding] = useState()
  const [selectedChat, setSelectedChat] = useState([])
  const [personChat, setPersonChat] = useState(false)
  const [chats, setChats] = useState([])
  console.log("first",userData)


  const handleSearchKeyPress = (e) => {
    if (e.key === 'Enter') {
      if(search === "") {
        setSearch("")
      }
      console.log(search)
      handleUsers();
      console.log("search",search)

    }
  };
  const handleUsers = async () => {
    if (!search) {
      console.log(search)
      setSearch('');
      localStorage.removeItem('search'); // Clear search from localStorage
      return; // Return early if search is empty
    }

    try {
      console.log("accessToken",userData.accessToken)
      const config = {
          headers: {
            Authorization :`Bearer ${userData.accessToken}`
          }
      }
      setLoding(true)
      const {data} = await axios.get(`http://localhost:5000/user?search=${search}`,config)
      console.log(data)
      setLoding(false)
      setSearchResult(data)
      localStorage.setItem('search', search);
    }catch(err) {
      console.error('Error during login:', err);
      setError('An error occurred during login.');
    }
  }
  console.log("userData", userData)

  // const accessChat = async (userId) =>{
  //   try {

  //     const config = {
  //       headers:{
  //         "Content-type":"application/json",
  //         Authorization:`Bearer ${userData.accessToken}`
  //       }
  //     }

  //     const {data} = await axios.post("http://localhost:5000/chat/one",{userId},config)
  //     console.log("this is post data",data)
  //     console.log(chats.find((c)=> c._id === data._id))
  //     if(!chats.find((c)=> c._id === data._id)) setChats([data,...chats])
  //     setSelectedChat(data)
  //     console.log("this is chat",data)
  //     console.log("this is selectedchat",selectedChat)
  //     setPersonChat(true)
  //   }catch(err) {
  //     console.error('Error during login:', err);
  //     setError('An error occurred');
  //   }
  // }
  const accessChat = async (userId) => {
    try {
      const config = {
        headers: {
          "Content-type": "application/json",
          Authorization: `Bearer ${userData.accessToken}`,
        },
      };

      // Check if the chat with the given user already exists in chats
      const existingChat = chats.find((c) => c.users.includes(userId));

      if (!existingChat) {
        // If the chat doesn't exist, create a new one
        const { data } = await axios.post("http://localhost:5000/chat/one", { userId }, config);
        setChats([data, ...chats]); // Add the new chat to the chats state
        setSelectedChat(data); // Set the new chat as the selected chat
      } else {
        // If the chat already exists, set it as the selected chat
        setSelectedChat(existingChat);
      }

      setPersonChat(true);
    } catch (err) {
      console.error('Error during login:', err);
      setError('An error occurred');
    }
  };
  useEffect(() => {
    console.log("selectedChat", selectedChat);
  }, [selectedChat]);
  useEffect(() => {
   handleUsers()
  }, [search]);
  return (
    <div className='chatpage'>
      <div className='chat-container'>


        <div className='chat-right'>
          <div className='right-container'>
            <div className='right-container-navbar'>
              <div className='nav-container'>
                <div className='profile'>
                  <div className='nav-profile-pic'>
                    <img src={userData.other.pic} alt="default" />
                  </div>
                </div>
                <div className='group-others'>
                  <div className='group-icons'>
                    <GroupsIcon className ="grpicons"/>
                    <MoreVertIcon className='grpicons' onClick={()=> setdropDown(!dropDown)}/>
                    {
                      dropDown && (
                        <div className='dropdown'>
                          <div className='dropBtn' >
                            My Profile
                          </div>
                          <div className='dropBtn' onClick={(e)=>handleLogout(e)}>
                            Log out
                          </div>
                        </div>
                      )
                    }

                  </div>
                </div>
              </div>
            </div>
            <div className='right-search-box'>
                {error && <p className="error-message">{error}</p>}
                <div className='search-container'>
                  <SearchIcon className='search-icon'/>
                  <input type="text" value={search} onChange={(e) => setSearch(e.target.value)}
                    onKeyPress={handleSearchKeyPress}
                  />
                  <button className='search-user-button' onClick={handleUsers}>Go</button>
                </div>
            </div>
            <div className='right-container-user-list'>
              <div className='list-container'>

                  {
                    loding ? (
                      <div>Loding</div>
                    ): (
                      <div className='user-list-container'>
                        <div className='user-list-item'>
                        {
                          searchResult.map((item,i)=> {
                            return (<>
                            <div className='user-item' key={i} onClick={() => accessChat(item._id)}>
                              <div className='user-item-profile'>
                                <div className='item-profile-pic'>
                                  <img src={item.pic} alt="default" />
                                </div>
                              </div>
                              <div className='user-item-data'>
                                <div className='user-item-identity'>
                                  <h3>{item.username}</h3>
                                  <p>16/07/2004</p>
                                </div>
                                <div className='lattest-msg'>
                                  <p>you: this is you messages</p>
                                </div>
                              </div>
                            </div>

                            </>
                            )
                          })
                        }
                        </div>
                      </div>

                    )
                  }



              </div>
            </div>

          </div>
        </div>


        <div className='chat-container-left'>
          {
            personChat ? <MyChat userData={userData} selectedChat={selectedChat} setSelectedChat={setSelectedChat} chats={chats} setChats={setChats} user={selectedChat.users.filter((u) => u._id !== userData._id)} /> :(
              <div className='whatsapp-home'>
                <div className='home-container'>
                  <div className='home-container-box'>
                    <div className='home-container-icon'>
                      <WhatsAppIcon className='icon'/>
                    </div>
                    <div className='home-container-para'>
                      <div>
                        <h1>WhatsApp Web</h1>
                      </div>
                      <div className="home-whatsapp-title">Send and receive messages without keeping your phone online.<br/>Use WhatsApp on up to 4 linked devices and 1 phone at the same time.</div>
                    </div>
                  </div>
                </div>
              </div>
            )
          }

        </div>  
      </div>      
    </div>
  )
}

export default ChatPage
