import React, { useEffect, useState,useRef } from 'react'
import GroupsIcon from '@mui/icons-material/Groups';
import './mychat.css'
import axios from 'axios';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import io from 'socket.io-client'

const socket = io.connect("http://localhost:5000")

const MyChat = ({userData,selectedChat, setSelectedChat,chats,setChats,user}) => {
  const [loggedUser, setLoggedUser] = useState([])
  const [inputMsg, setInputMsg] = useState("")
  const [message, setMessage] = useState({ data: [] });
  const [socketConnected, setSocketConnected] = useState(false)


  let selectedChatCompare


  const chatBodyRef = useRef();
  // console.log("I dont want this",user)
  const myUser = user.find(u => u._id !== userData.other._id);
  // console.log("myUser",myUser)
//  console.log("fetche message",message)

 const fetchMessages = async() => {
  try {
    const config ={
      headers: {
        Authorization:`Bearer ${userData.accessToken}`
      }
    }

    const response = await axios.get(`http://localhost:5000/message/${selectedChat._id}`, config);
    const responseData = response.data;

    // console.log("this is the message I want", responseData);

    setMessage({ data: responseData });
    // console.log("responseData:",responseData)
  } catch (err) {
    console.log(err);
  }
}

  useEffect(()=> {
    fetchMessages()
    console.log("useEffect set message",message)

  },[selectedChat])


  const fetchChats = async () => {
    console.log("user",myUser)
    try {
      const config ={
        headers: {
          Authorization:`Bearer ${userData.accessToken}`
        }
      }

      const data = await axios.get("http://localhost:5000/chat/one",config)
      setChats(data)
      // console.log("this is the chat I want",data)
    }catch(err) {
      console.log(err)
    }
  }


  const sendMessage = async () => {
    try {
      const config = {
        headers: {
          "Content-Type":"application/json",
          Authorization:`Bearer ${userData.accessToken}`
        }
      }
      setInputMsg("")
      const {data} = await axios.post(
        "http://localhost:5000/message",
        {
          content:inputMsg,
          chatId: selectedChat._id,
        },
        config
      )

      // console.log("this is msg",data)


    // Update the state using the current state
    setMessage((prevState) => {
      return {
        data: [...prevState.data, data],
      };
    });

    }catch(err) {

    }
  }
  useEffect(()=> {
    socket.emit("setup",userData.other)
    socket.on("connected", ({ userId }) => {
      setSocketConnected(true);
      console.log(`User connected: ${userId}`);
    });
  },[])

  useEffect(() => {
    const chatBody = document.getElementById('chat-body');
    if (chatBody) {
      chatBody.scrollTop = chatBody.scrollHeight;
    }
  }, [message.data]);


  useEffect(() => {
    chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
  });


  const handleSubmit = (event) =>{
    setInputMsg(event.target.value)
    console.log("handleSubmt",message)
  }
  useEffect(()=> {
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      // console.log("hi",storedUserData)
      // console.log("hello",JSON.parse(storedUserData))
      setLoggedUser(JSON.parse(storedUserData));
    }
    fetchChats()

  },[selectedChat])

  console.log("logged user",loggedUser)
  return (
    <div className='chat-left'>
      <div className='left-container'>
        <div className='right-container-navbar'>
          <div className='nav-container'>
            <div className='profile'>
              <div className='nav-profile-pic'>
                <img src={userData.other.pic} alt="default" />
              </div>
              <div className='selected-user-name'>

                <h3>{myUser.username}</h3>

              </div>

            </div>
            <div className='group-others'>
              <div className='group-icons'>
                <GroupsIcon className ="grpicons"/>
                {/* <MoreVertIcon className='grpicons' onClick={()=> setdropDown(!dropDown)}/> */}


              </div>
            </div>
          </div>
        </div>
        <div className='chat-body' ref={chatBodyRef}>
          {console.log(message.data)}
        {message.data && message.data.map((item, i) => {
          const isMyMessage = item.sender._id === userData.other._id;
          const messageContainerClass = isMyMessage ? 'message right-message' : 'message left-message';

          return (
            <div className={messageContainerClass} key={i}>
              {/* id={username === item.author ?"you":"other"} */}
              <div className="message-container">
                <div className='message-content'>
                  <p>{item.content}</p>
                </div>
                <div className='message-meta'>
                  <div className='time'>{item.createdAt}</div> {/* Assuming you have a createdAt property */}
                  <div className='authors'>{item.sender.username}</div>
                </div>
              </div>
            </div>
          );
        })}

      </div>
      <div className="chat-footer">
        <div className='chat-footer-container'>
            <input type="text" placeholder='Hey...'
            value={inputMsg}
            onChange={(event)=> {
              handleSubmit(event)
            }}
            onKeyPress={(event) => {
              if (event.key === "Enter" || event.keyCode === 13) {
                sendMessage()
              }
            }}
            />
        </div>  
      </div>
      </div>
    </div>
  )
}

export default MyChat
