import React, { useState } from 'react';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import './register.css'
import { Link } from 'react-router-dom';
const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null, // Store the selected image file
  });

  const [show, setShow] = useState(false)
  const showPassword = (e) => {
    setShow(!show)
  }
  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    // Check if the input is a file input and handle it separately
    if (type === 'file') {
      setFormData({
        ...formData,
        [name]: files[0], // Store the selected image file
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here, e.g., send data to the server
    console.log(formData);
  };

  return (
    <div className="register">
      <div className="register-container">
        <div className='register-body'>
          <div className="profile-pic">
            <WhatsAppIcon className='icon'/>
          </div>
          <div className="form">
            <h3>Create New Account</h3>
            <form onSubmit={handleSubmit}>
              <div className="input-feild">
                <label htmlFor="username">Name</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  placeholder="Username"
                  value={formData.username}
                  onChange={handleChange}
                />
              </div>
              <div className="input-feild">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
              <div className="input-feild">
                <label htmlFor="password">Password</label>
                <div className='passwordtype'>
                  <input
                    type={show ? "text" : "password"}
                    id="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                  />
                  <button onClick={showPassword}>
                    {formData.password === "" ? "Show" :show ? "Hide" : "Show"}
                  </button>
                </div>

              </div>
              <div className="input-feild">
                <label htmlFor="pic">Upload your pic</label>
                <input
                  style={{fontSize:"12px"}}
                  type="file"
                  name="profileImage"
                  accept="image/*" // Restrict to image files
                  onChange={handleChange}
                />
              </div>
              <div className='button'>
                 <button button type="submit">Sign Up</button>
              </div>

            </form>
            <div className='loginroute'>
              <p>If you already registered: </p>
              <Link>
                Login
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
