import React, { useState } from 'react';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import {useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../register/register.css'
import { Link } from 'react-router-dom';
const Login = ({change, setChange}) => {
  const history = useNavigate()
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [error, setError] = useState(null); 
  const [show, setShow] = useState(false)
  const showPassword = (e) => {
    setShow(!show)
  }
  const handleChange = (e) => {
    const { name, value} = e.target;
    // Check if the input is a file input and handle it separately

      setFormData({
        ...formData,
        [name]: value,
      });
  };

  const handleSubmit = async (e) => {
    if (!formData.username || !formData.email || !formData.password) {
      setError('Please fill in all required fields.');
      return; // Stop the submission if any field is empty
    }
    try {
      const response = await axios.post(
        'http://localhost:3001/user/login',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );
      console.log("response:",response)
      if (response.status === 200 || response.status === 201) {
        console.log('login successful:', response.data);
        history('/');

      } else {
        console.error('login failed:', response.data);
        setError(response.data.error); // Set the error message from the API response
      }
    } catch (error) {
      console.error('Error during registration:', error);
      setError('Email is already exist:'); // Set a generic network error message
    }
  };


  return (
    <div className="register">
      <div className="register-container">
        <div className='register-body'>
          <div className="profile-pic">
            <WhatsAppIcon className='icon'/>
          </div>
          <div className="form">
            <h3>Login Account</h3>
            <form onSubmit={handleSubmit}>
              <div className="input-feild">
                <label htmlFor="username">Name</label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  placeholder="Username"
                  value={formData.username}
                  onChange={handleChange}
                />
              </div>

              <div className="input-feild">
                <label htmlFor="password">Password</label>
                <div className='passwordtype'>
                  <input
                    type={show ? "text" : "password"}
                    id="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                  />
                  <button onClick={showPassword}>
                    {formData.password === "" ? "Show" :show ? "Hide" : "Show"}
                  </button>
                </div>

              </div>
              {error && <p className="error-message">{error}</p>}
              <div className='button'>
                 <button button type="submit">Login</button>
              </div>

            </form>
            <div className='loginroute'>
              <p>Create an Account: </p>
              <Link onClick={()=> setChange(!change)}>
                Sign up
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
