import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';
import Home from './pages/Home';
// import Register from './pages/Register.jsx';
import Authentication from './pages/Authentication';
import Login from './component/login/Login';
import Register from './component/register/Register';
import { useEffect, useState } from 'react';
import ChatPage from './component/chatpage/ChatPage';
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));
  const [userData, setUserData] = useState({});

  // Function to set the login state and store user data
  const handleLogin = (data) => {
    setIsLoggedIn(true);
    setUserData(data);

    // Store user data in localStorage
    localStorage.setItem('userData', JSON.stringify(data));
  };

  // Function to set the logout state and clear user data from localStorage
  const handleLogout = () => {
    setIsLoggedIn(false);

    // Clear user data from localStorage
    localStorage.removeItem('userData');
  };

  useEffect(() => {
    // const storedToken = localStorage.getItem('token');
    // if (storedToken) {
    //   setIsLoggedIn(true);
    // }

    // Retrieve user data from localStorage, if available
    console.log(isLoggedIn)
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      // console.log("hi",storedUserData)
      // console.log("hello",JSON.parse(storedUserData))
      setUserData(JSON.parse(storedUserData));
    }

    window.addEventListener('beforeunload', () => {
      localStorage.clear();
    });

    // // Remove the event listener when the component unmounts
    // return () => {
    //   window.removeEventListener('beforeunload', () => {
    //     localStorage.clear();
    //   });
    // };
  }, []);
  return (
    <Router>
      <Routes>
        <Route
            exact
            path="/"
            element={<Authentication/>}
        />
        <Route
          exact
          path="/chat"
          element={isLoggedIn ? <ChatPage userData = {userData}/> : <Home handleLogin={handleLogin} isLoggedIn={isLoggedIn} />}
        />
        <Route exact path="/register" element={<Register/>} />
      </Routes>
    </Router>
  );
}

export default App;
